              </header>
              <main className="flex-1 overflow-auto">
                <AdminRouter />
              </main>
            </div>
          </div>
        </SidebarProvider>
        <FullScreenLoader 
          isLoading={isLoading}
          message={loadingMessage}
          showProgress={showProgress}
          progress={progress}
        />
      </>
    );
  }

  return (
    <>
      <PublicRouter />
      <ChatbotWidget />
      <FullScreenLoader 
        isLoading={isLoading}
        message={loadingMessage}
        showProgress={showProgress}
        progress={progress}
      />
    </>
  );
}

function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <LoadingProvider>
        <TooltipProvider>
          <Toaster />
          <AppContent />
        </TooltipProvider>
      </LoadingProvider>
    </QueryClientProvider>
  );
}

export default App;

