optimizedCSS:
/* Optimized Font Loading CSS */

@font-face {
  font-family: 'Base Neue';
  src: url('/fonts/base-neue-regular.woff2') format('woff2'),
       url('/fonts/base-neue-regular.woff') format('woff');
  font-weight: 400;
  font-style: normal;
  font-display: swap;
  font-feature-settings: 'kern' 1, 'liga' 1;
}

@font-face {
  font-family: 'Base Neue';
  src: url('/fonts/base-neue-medium.woff2') format('woff2'),
       url('/fonts/base-neue-medium.woff') format('woff');
  font-weight: 500;
  font-style: normal;
  font-display: swap;
  font-feature-settings: 'kern' 1, 'liga' 1;
}

@font-face {
  font-family: 'Base Neue';
  src: url('/fonts/base-neue-semibold.woff2') format('woff2'),
       url('/fonts/base-neue-semibold.woff') format('woff');
  font-weight: 600;
  font-style: normal;
  font-display: swap;
  font-feature-settings: 'kern' 1, 'liga' 1;
}

@font-face {
  font-family: 'Base Neue';
  src: url('/fonts/base-neue-bold.woff2') format('woff2'),
       url('/fonts/base-neue-bold.woff') format('woff');
  font-weight: 700;
  font-style: normal;
  font-display: swap;
  font-feature-settings: 'kern' 1, 'liga' 1;
}

@font-face {
  font-family: 'Base Neue Mono';
  src: url('/fonts/base-neue-mono-regular.woff2') format('woff2'),
       url('/fonts/base-neue-mono-regular.woff') format('woff');
  font-weight: 400;
  font-style: normal;
  font-display: swap;
  font-feature-settings: 'liga' 0, 'calt' 0;
}

/* Font Loading Optimization */
.font-loading {
  font-display: swap;
  font-variation-settings: normal;
}

.font-loaded {
  font-display: auto;
}

/* Fallback Font Metrics Matching */
@font-face {
  font-family: 'Base Neue Fallback';
  src: local('Inter'), local('system-ui');
  ascent-override: 90%;
  descent-override: 22%;
  line-gap-override: 0%;
  size-adjust: 107%;
}
